class QIHTMLElement{constructor(e="div"){this.element=document.createElement(e)}addchildren(e){return e.forEach(e=>{e&&this.element.appendChild(e)}),this}addClass(e){return"string"==typeof e?this.element.classList.add(e):e.forEach(e=>{this.element.classList.add(e)}),this}setAttribute(e){for(var[t,n]of Object.entries(e))this.element.setAttribute(t,n);return this}setText(e){return this.element.textContent=e,this}setHTML(e){return this.element.innerHTML=e,this}appendChild(e){if(e instanceof QIHTMLElement)this.element.appendChild(e.element);else{if(!(e instanceof Node))throw new Error("El hijo debe ser una instancia de HTMLElement o Node.");this.element.appendChild(e)}return this}appendTo(e){if(e instanceof QIHTMLElement)e.element.appendChild(this.element);else{if(!(e instanceof Node))throw new Error("El padre debe ser una instancia de HTMLElement o Node.");e.appendChild(this.element)}return this}getElement(){return this.element}}